// Компонент ссылки
function FooterLink({
	href,
	children,
	small = false,
}: {
	href: string
	children: React.ReactNode
	small?: boolean
}) {
	return (
		<Link
			href={href}
			className={`${
				small ? 'text-xs' : 'text-sm'
			} text-gray-500 hover:text-[#F89514] transition-colors`}
		>
			{children}
		</Link>
	)
}

// Компонент контакта
function ContactItem({
	icon,
	children,
}: {
	icon: React.ReactNode
	children: React.ReactNode
}) {
	return (
		<div className='flex items-start group'>
			<span className='flex-shrink-0 h-5 w-5 text-gray-400 mr-2 mt-0.5 group-hover:text-[#F89514] transition-colors'>
				{icon}
			</span>
			<span className='text-sm text-gray-500 group-hover:text-[#F89514] transition-colors'>
				{children}
			</span>
		</div>
	)
}

// Компонент соц-ссылки
function SocialLink({
	href,
	children,
}: {
	href: string
	children: React.ReactNode
}) {
	return (
		<a
			href={href}
			className='text-gray-400 hover:text-[#F89514] transition-colors'
		>
			{children}
		</a>
	)
}

